const fs = require('fs');

// Read the template
const template = fs.readFileSync('/job/operating_system/LEASE_RENT_HOUSE_TEMPLATE.html', 'utf-8');

// Define replacements
const replacements = {
  '{{CONTACT_PHONE}}': '(972) 632-2787',
  '{{DATE}}': 'February 17, 2026',
  '{{TENANT_NAME}}': 'Christian Lachausse',
  '{{PROPERTY_ADDRESS}}': '111 E Main Street, Celina, TX 75009',
  '{{LEASE_START}}': 'February 17, 2026',
  '{{MONTHLY_RENT}}': '$1,800.00',
  '{{SECURITY_DEPOSIT}}': 'No security deposit required.',
  '{{UTILITIES_SECTION}}': 'Tenant will be invoiced monthly for actual utility bills after TJM receives them. Invoices will be sent following receipt of utility bills by the Landlord.',
  '{{CUSTOM_TERMS}}': 'Mowing of yard is included in the base rent. Tenant is not responsible for lawn care.',
  '{{CONTACT_EMAIL}}': 'Sloan@tjmfuneral.com'
};

// Replace all placeholders
let rendered = template;
for (const [placeholder, value] of Object.entries(replacements)) {
  rendered = rendered.replace(new RegExp(placeholder.replace(/[{}]/g, '\\$&'), 'g'), value);
}

// Write the rendered HTML
fs.writeFileSync('/job/tmp/lease.html', rendered);
console.log('Lease HTML rendered successfully');

